import React, { Component } from "react";
import CourseGoal from "./components/Goal";
import "./App.css";
import GoalImage from "./assets/reactjs.png";
import Header from "./components/Header";

type GoalType = {
  id: number;
  title: string;
  description: string;
};

function App() {
  const [goals, setGoals] = React.useState<GoalType[]>([]);
  function handleAddGoal(): void {
    setGoals((prevGoals) => {
      const newGoals: GoalType = {
        id: prevGoals.length,
        title: "HU HU KHEC KHEC",
        description: "KHEC KHEC HU HU",
      };
      return [...prevGoals, newGoals];
    });
  }
  return (
    <main>
      <Header image={{ src: GoalImage, alt: "A List Of Goal" }} />
      <button onClick={() => handleAddGoal()}></button>
      <ul>
        {goals.map((item) => {
          return (
            <li key={item.id}>
              <CourseGoal
                title={item.title}
                description={item.description}
                addGoalFunction={() => handleAddGoal()}
              >
                <p>hell</p>
              </CourseGoal>
            </li>
          );
        })}
      </ul>
    </main>
  );
}

export default App;
