import { ReactNode } from "react";
import InfoBox from "./InfoBox";

type CourseGoal = {
  id: number;
  title: string;
  description: string;
};

type CourseGoalsListProps = {
  goals: Array<CourseGoal>;
  deleteGoalFunction: (id: number) => void;
};

export default function CourseGoalsList({
  goals,
  deleteGoalFunction,
}: CourseGoalsListProps) {
  if (goals.length === 0) {
    return (
      <InfoBox mode="hint" severity="low">
        You have no course goals yet. Start adding some!
      </InfoBox>
    );
  }
  let warningBox: ReactNode;

  if (goals.length >= 4) {
    warningBox = (
      <InfoBox mode="warning">
        You're collecting a lot of goals. Don't put too much on your plate
      </InfoBox>
    );
  }

  return (
    <>
      {warningBox}
      <ul>
        {goals.map((item) => {
          return (
            <li key={item.id}>
              <article>
                <div>
                  <h2>{item.id}</h2>
                  <h2>{item.title}</h2>
                  <p>{item.description}</p>
                </div>
                <button onClick={() => deleteGoalFunction(item.id)}>
                  Delete
                </button>
              </article>
            </li>
          );
        })}
      </ul>
    </>
  );
}
