import React, { Component } from "react";
import CourseGoal from "./components/Goal";
import "./App.css";
import GoalImage from "./assets/reactjs.png";
import Header from "./components/Header";

type GoalType = {
  id: number;
  title: string;
};

function App() {
  const [goals, setGoals] = React.useState<GoalType[]>([]);
  function handleAddGoal(): void {
    setGoals((prevGoals) => {
      const newGoals: GoalType = {
        id: Math.random(),
        title: "HU HU KHEC KHEC",
      };
      return [...prevGoals, newGoals];
    });
  }
  return (
    <main>
      <Header image={{ src: GoalImage, alt: "A List Of Goal" }} />
      <CourseGoal
        title="COURSE GOAL"
        description="DESCRIPTION"
        addGoalFunction={() => handleAddGoal()}
      >
        <p>123</p>
      </CourseGoal>
      {goals.map((item) => {
        return <h1>{item.title}</h1>;
      })}
    </main>
  );
}

export default App;
