import React, { Component } from "react";
import CourseGoal from "./components/Goal";
import "./App.css";
import GoalImage from "./assets/reactjs.png";
import Header from "./components/Header";

type GoalType = {
  id: number;
  title: string;
  description: string;
};

function App() {
  const [goals, setGoals] = React.useState<GoalType[]>([]);
  function handleAddGoal(): void {
    setGoals((prevGoals) => {
      const newGoals: GoalType = {
        id: Math.random(),
        title: "HU HU KHEC KHEC",
        description: "KHEC KHEC HU HU",
      };
      return [...prevGoals, newGoals];
    });
  }
  return (
    <main>
      <Header image={{ src: GoalImage, alt: "A List Of Goal" }} />
      <CourseGoal
        title="COURSE GOAL"
        description="DESCRIPTION"
        addGoalFunction={() => handleAddGoal()}
      >
        <p>123</p>
      </CourseGoal>
      <ul>
        {goals.map((item) => {
          return (
            <li key={item.id}>
              <h1>{item.title}</h1>
              <p>{item.description}</p>
            </li>
          );
        })}
      </ul>
    </main>
  );
}

export default App;
