import React, { Component } from "react";
import CourseGoal from "./components/Goal";
import "./App.css";
import GoalImage from "./assets/reactjs.png";
import Header from "./components/Header";
import NewGoal from "./components/NewGoal";

type GoalType = {
  id: number;
  title: string;
  description: string;
};

function App() {
  const [goals, setGoals] = React.useState<GoalType[]>([]);
  function handleAddGoal(title: string, description: string): void {
    setGoals((prevGoals) => {
      const newGoal: GoalType = {
        id: prevGoals.length,
        title,
        description,
      };
      return [...prevGoals, newGoal];
    });
  }
  function handleDeleteGoal(id: number): void {
    setGoals((prevGoals) => {
      let newGoals = new Array<GoalType>();
      for (let i = 0; i < id; i++) {
        newGoals.push(prevGoals[i]);
      }
      for (let i = id + 1; i < prevGoals.length; i++) {
        let newGoal = prevGoals[i];
        newGoal.id = i - 1;
        newGoals.push(newGoal);
      }
      return newGoals;
    });
  }
  return (
    <main>
      <Header image={{ src: GoalImage, alt: "A List Of Goal" }} />
      <NewGoal />
      <ul>
        {goals.map((item) => {
          return (
            <li key={item.id}>
              <CourseGoal
                id={item.id}
                title={item.title}
                description={item.description}
                deleteGoalFunction={handleDeleteGoal}
              >
                <p>hell</p>
              </CourseGoal>
            </li>
          );
        })}
      </ul>
    </main>
  );
}

export default App;
